           get { return m_iNoForms; }            set { m_iNoForms = value; }        }                /// <summary>        /// No Campaign        ///         /// </summary>        protected int m_iNoCampaign = 0;        public virtual int iNoCampaign        {            get { return m_iNoCampaign; }            set { m_iNoCampaign = value; }        }                /// <summary>        /// Memeory Space        ///         /// </summary>        protected int m_iMemeorySpace = 0;        public virtual int iMemeorySpace        {            get { return m_iMemeorySpace; }            set { m_iMemeorySpace = value; }        }                /// <summary>        /// No Sub User        ///         /// </summary>        protected int m_iNoSubUser = 0;        public virtual int iNoSubUser        {            get { return m_iNoSubUser; }            set { m_iNoSubUser = value; }        }                /// <summary>        /// No Input Controle        ///         /// </summary>        protected int m_iNoInputControle = 0;        public virtual int iNoInputControle        {            get { return m_iNoInputControle; }            set { m_iNoInputControle = value; }        }                /// <summary>        /// Is Branding        ///         /// </summary>        protected bool m_bIsBranding = false;        public virtual bool bIsBranding        {            get { return m_bIsBranding; }            set { m_bIsBranding = value; }        }                /// <summary>        /// No Email Sent        ///         /// </summary>        protected int m_iNoEmailSent = 0;        public virtual int iNoEmailSent        {            get { return m_iNoEmailSent; }            set { m_iNoEmailSent = value; }        }                /// <summary>        /// No Responses        ///         /// </summary>        protected int m_iNoResponses = 0;        public virtual int iNoResponses        {            get { return m_i